query GetContentPage($slug: String!) {
  contentPageFieldses(where: {
    published: {
      equals: true
    }
    slug: {
      equals: $slug
    }
  }) {
    id
    cmsId
    contentPageId
    footer
    layout
    leftColumn
    mainContent
    parentContentPageId
    published
    rightColumn
    slug
    title
    topBar
    updatedAt
  }
}
