query GetCategoriesByTokenType($tokenTypeIds: [String!]!) {
  tokenTypes(
    where: {
      id: { in: $tokenTypeIds }
      categories: { every: { published: { equals: true } } }
    }
  ) {
    id
    fields {
      id
      name
      order
    }
    categories(orderBy: {
      order: {
        sort: asc
       }}
    ) {
      id
      name
      order
      category {
        id
      }
    }
  }
}
