query GetSubCategoriesByCategory($categoryIds: [String!]!) {
  subCategoryFieldses(
    where: {
      categories: { some: { id: { in: $categoryIds } } }
      published: { equals: true }
    }
    orderBy: [{
       order: {
        sort: asc
       }
    }]
  ) {
    id
    name
    order
    subCategory {
      id
    }
  }
}
