query:
query GetAllCryptoResponsesByIds(
  $locationIds: [String!]!
  $tokenTypeIds: [String!]!
  $categoryIds: [String!]!
  $subCategoryIds: [String!]!
  $methodIds: [String!]
  $pageNo: Int!
  $pageSize: Int!
) {
  groupedResponses(
    where: {
      locations: $locationIds
      tokenTypes: $tokenTypeIds
      categories: $categoryIds
      subCategories: $subCategoryIds
      methods: $methodIds
    }
    page: $pageNo
    pageSize: $pageSize
  ) {
    page
    totalPages
    permissions
    definitionData
    data {
      parentId
      name
      categories {
        parentId
        name
        subCategories {
          parentId
          name
          jurisdictions {
            isEEA
            parentId
            name
            methods {
              parentId
              name
              rag
              text
              comment
            }
          }
        }
      }
    }
  }
}
